				}
				else
				{
					numOfO++;
				}
				if(numOfx==SIZE|| numOfO== SIZE)
				{
					gotResult=true;
				}
		}	
			}
			//检查反对角
			if(!gotResult)
			{
				 numOfx=0;
				 numOfO=0;
				for(int i=0;i<SIZE;i++)
				{
					if(board[i][SIZE-1-i]==1)
					{
						numOfx++;
					}
					else
					{
						numOfO++;
					}
					if(numOfx==SIZE|| numOfO== SIZE)
					{
						gotResult=true;
					}


